Menu = new Mongo.Collection('menu');

Menu.allow({
    insert: function (userId, item){
        return userId && item.owner === userId;
            },
    update: function (userId, item, fields, modifier){
        return true;
            },
    remove: function (userId, item){
        return userId && item.owner == userId;
            }
});
