angular.module('todayspecial')
        .controller('login',['$scope','$meteor','$stateParams',
                function($scope,$meteor,$stateParams){

                    $scope.login = function(data){
                       //Meteor.loginWithPassword(data.email, password)     
                        //var usr = Accounts.findUserByEmail(data.email);
                        Meteor.loginWithPassword(data.email, data.password,function(c){
                            console.log(c);
                        })
                    }

                }
        ]);
