angular.module('menuboards')
        .controller('dashboard',['$scope','$meteor','$stateParams',
                function($scope,$meteor,$stateParams){

                    var errs = {
                        signin: false
                    }
                    $scope.errors = errs;

                    var states = {
                        editItem:false
                    }
                    $scope.states = states;

                    $scope.signIn = function(data){
                        //console.log(data.length);
                        if(data!=undefined){
                            if(data.email && data.password){
                                Meteor.loginWithPassword(data.email,data.password,function(err){
                                    if(err.error==400 || err.error==403){
                                        errs.signin=true
                                    }
                                });
                            }else{
                                errs.signin=true
                            }
                        }else{
                           errs.signin=true
                        }
                    }
                    $scope.signOut = function(){
                        Meteor.logout();
                    };

                    $scope.menu = $meteor.collection(Menu).subscribe('menu');

                    $scope.edit = function(item){
                        $scope.newItem = $meteor.object(Menu,item._id,false);
                        states.editItem=true; 
                    }

                    $scope.save = function(){
                        $scope.newItem.save();
                        states.editItem=false;
                    }
                    $scope.xEditItem = function(){
                        states.editItem=false;
                    }

                    $scope.remove = function(item){
                        $scope.menu.splice($scope.menu.indexOf(item),1);
                    }

                }
        ]);
