angular.module('todayspecial')
        .controller('login',['$scope','$meteor','$stateParams',
                function($scope,$meteor,$stateParams){
                    $scope.errs = {
                        signin:false
                    };

                    $scope.login = function(data){
                        if(data!=undefined){
                            if(data.email && data.password){
                                Meteor.loginWithPassword(data.email,data.password,function(err){
                                    if(err.error==400 || err.error==403){
                                        errs.signin=true
                                    }
                                });
                            }else{
                                errs.signin=true
                            }
                        }else{
                           errs.signin=true
                        }
                    }
                    console.log($scope.errs);

                }
        ]);
